fn main() {
    let input = HK_INPUT.to_string();
    println!("Hello, world!  Day 1 2015: {}", input.len());
    println!("{}\n{}", floor(&input), basement_pos(&input));
}

// ((( and (()(()( both result in floor 3.
// ))((((( also results in floor 3.
// ()) and ))( both result in floor -1 (the first basement level).
// ))) and )())()) both result in floor -3.

fn floor(instructions: &String) -> i32 {
    let mut floor = 0;
    for c in instructions.chars() {
        if c == '(' {
            floor += 1;
        } else if c == ')' {
            floor -= 1;
        }
    }
    return floor;
}

fn basement_pos(instructions: &String) -> i32 {
    let mut floor = 0;
    let mut pos = 1;
    for c in instructions.chars() {
        if c == '(' {
            floor += 1;
        } else if c == ')' {
            floor -= 1;
        }
        if floor == -1 {
            return pos;
        }
        pos += 1;
    }
    return pos;
}

#[test]
fn test_floor() {
    assert_eq!(3, floor(&"(((".to_string()));
    assert_eq!(3, floor(&"))(((((".to_string()));
    assert_eq!(3, floor(&"(()(()(".to_string()));
    assert_eq!(-1, floor(&"())".to_string()));
    assert_eq!(-1, floor(&"))(".to_string()));
    assert_eq!(-3, floor(&")))".to_string()));
    assert_eq!(-3, floor(&")())())".to_string()));
}

